INSERT INTO post (id, title, content, creation_date) VALUES(1, 'LES MODIFICATEURS D''ACCÈS', 'En java, la mise en application du concept d''encapsulation est possible en parti grâce aux modificateurs d''accès. En effet, ils nous permettent de définir la visibilité, la portabilité et l''intégrité des données traitées.', '2019-08-02 10:15');
INSERT INTO post (id, title, content, creation_date) VALUES(2,'A QUOI SERT LE MOT CLÉ  STATIC  ?', 'Un attribut déclaré  static , également appelé attribut de classe, est indépendant des objets instanciés et pourrait servir à partager des informations entre les instances de cette classe.','2019-04-10 11:15');
INSERT INTO post (id, title, content, creation_date) VALUES(3,'QUE PERMET LE MOT CLÉ « VOLATILE » ?', 'Dans un contexte Multithread, on déclare une variable « volatile » si elle est susceptible d''être lue/modifiée (de manière asynchrone) simultanément par plusieurs threads.','2019-02-11 12:15');
